import ar.com.zauber.commons.base.Predicate;
import ar.com.zauber.commons.base.predicate.*;
String name = "Shirley";
Predicate<String> nameEmma = new EqualsPredicate( "Emma" );
Predicate<String> nameShirley = new EqualsPredicate( "Shirley" );
// Testing all predicates for "Shirley"
System.out.println( "Is Name Emma?: " + nameEmma.evaluate( name ) );
System.out.println( "Is Name Shirley?: " + nameShirley.evaluate( name ) );
